import logo from './logo.svg';
import './App.css';
import React, { useEffect, useState } from "react";

// import ...

const App = () => {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [emailDirty, setEmailDirty] = useState(false)
  const [passwordDirty, setPasswordDirty] = useState(false)
  const [emailError, setEmailError] = useState('Email не пожет быть пустым')
  const [passwordError, setPasswordError] = useState('Пароль не может быть пустым')

  const blurHandler = (e) => {
      switch (e.target.name){
          case 'email':
              setEmailDirty(true)
              break
          case 'password':
              setPasswordDirty(true)
              break
      }
  }

  return (
  <div className='app'>
    <div className='reg'>
      <form id="singin">
      <h1>Регистрация</h1>
      {(emailDirty && password) && <div style={{color:"red"}}>{emailError}</div>}
      <input onBlur={(e)=>blurHandler(e)} name='email' type="text" placeholder='Enter your email…'/> 
      <label></label>
      <br></br>
      {(emailDirty && password) && <div style={{color:"red"}}>{passwordError}</div>}
      <input onBlur={(e)=>blurHandler(e)} name='password' type="password" placeholder='Enter your password…'/> 
      <label></label>
      <br></br>
      <button type='submit'>Registration</button>
      </form>
      </div>
  </div>
  );
};

export default App;
