import { useState } from "react"
import {useForm} from "react-hook-form"
import {useHistory} from "react-router-dom"
import "./singin.css"

function Singin(){

const history = useHistory()
const {register, handleSubmit, reset, formState: {errors, isValid}} = useForm({ mode:"onBlur"})

const [result, setResult] = useState({ message: '',success: false})

async function onSubmit(values) {
  useHistory('/')

  const response = await fetch('http://127.0.0.1:8000/auth/token', {
    method: 'POST',
    mode:'cors',
    credentials: 'include',
    headers: {
      'Access-Control-Allow-Origin' : 'http://127.0.0.1:8000/auth/token',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(values)
  })

  const result = await response.json()

  // обновляем состояние
  setResult({
    message: result,
    success: response.ok
  })
};

// нажатие кнопки сброса полей в исходное состояние приводит к перезагрузке страницы
function onClick() {
  window.location.reload()
};

// const onSubmit = (data) =>{

//   alert(JSON.stringify(data));
//   reset();

// }

    return (
        <div className='app'>     
          <div class='e3_7011'>
            <h1  class="e3_6976">Log In Daily Shelf</h1>
          </div>
          
          <form id="singin" onSubmit={handleSubmit(onSubmit)}>
              <div class='e2_6795'>
                <div class='e3_6883'>
                  <span  class="e3_6897">Username or email address</span>
                    <div class='ei3_6899_1_1035'>
                       
                        <input {...register('email',{required: " Это поле обязательно"})}/>

                        {errors.email && <span className="error_text">{errors.email.message}</span>}

                        <span  class="e3_6898">Password</span>
                        <div className='password'>
                         
                        <input {...register('password',{required:" Это поле обязательно", 
                                                          minLength: {value: 4, message: " Пароль должен быть длиннее"}})} 
                                                          type="password"/> 
                        {errors.password && <span className="error_text">{errors.password.message}</span>}
                        </div>   
                      </div>  
                      <button disabled={!isValid} type='submit' class="ei6_1793_3_6922_1_3106">Log In</button>
                      <span  class="ei3_6903_1_1144_1_1023">Forgot password</span>   
                    </div>
                      
                  </div>
              </form>

              <div  class="e3_7006">
                    <span  class="e3_7008">New to Daily Shell?  <a href="/singup" class="e3_7009">Create an account </a></span>
              </div>

        </div>
        );

};

export default Singin;
